mapper.select:
select menu.id, menu.name, menu.par_id, m.name as par_name, menu.name_tree, menu.enabled_yn from menu
inner join menu as m
where menu.name_tree like #{gnbName} and menu.name like #{name} and m.id=menu.par_id order by menu.sort_order desc;

mapper.update:
update menu set par_id = #{parId}, comp_id = #{compId}, name = #{name}, enabled_yn = #{enabledYN}, icon_url = #{iconUrl},
sort_order = #{sortOrder}, name_tree = #{nameTree}, id_tree = #{idTree}
where id = #{id};
